            <div class="card">
                <div class="card-header">
                    <h2>Problem Input</h2>
                </div>
                <div class="card-content">
                    <!-- Existing input elements -->
                </div>
            </div>

            <div class="column">
                <div class="card">
                    <div class="card-header">
                        <h2>Solution Metrics</h2>
                    </div>
                    <div class="card-content">
                        <div class="statistics-grid">
                            <!-- Existing metrics elements -->
                        </div>
                    </div>
                </div>
                <div class="card">
                    <div class="card-header">
                        <h2>Solution Visualization</h2>
                    </div>
                    <div class="card-content">
                        <canvas id="solution-canvas" width="800" height="600"></canvas>
                    </div>
                </div>
            </div>
        </div>
    </div>
    <script src="scripts.js"></script>
</body>
</html>
