    <script>
        document.getElementById('solve').addEventListener('click', function() {
            const algorithm = document.getElementById('algorithm').value;
            const vehicles = document.getElementById('vehicles').value;
            const capacity = document.getElementById('capacity').value;
            const locations = document.getElementById('locations').value;

            const data = {
                algorithm: algorithm,
                vehicles: vehicles,
                capacity: capacity,
                locations: locations
            };

            fetch('/run', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(data)
            })
            .then(response => response.text())
            .then(data => {
                alert(data);
            })
            .catch(error => {
                console.error('Error:', error);
            });
        });

        document.getElementById('input-method').addEventListener('change', function() {
            const manualInput = document.getElementById('manual-input');
            const fileInput = document.getElementById('file-input');
            if (this.checked) {
                manualInput.style.display = 'block';
                fileInput.style.display = 'none';
            } else {
                manualInput.style.display = 'none';
                fileInput.style.display = 'block';
            }
        });

        document.getElementById('add-location').addEventListener('click', function() {
            const xCoord = document.getElementById('x-coord').value;
            const yCoord = document.getElementById('y-coord').value;
            const locations = document.getElementById('locations');
            locations.value += `(${xCoord}, ${yCoord})\n`;
        });
    </script>
</body>
</html>
