from flask import Flask, request, jsonify
import verypy.cvrp_io as cvrp_io
import os

app = Flask(__name__)

@app.route('/parse_vrp', methods=['POST'])
def parse_vrp():
    if 'file' not in request.files:
        return jsonify({"error": "No file part"}), 400
    file = request.files['file']
    if file.filename == '':
        return jsonify({"error": "No selected file"}), 400
    if file:
        file_path = os.path.join("/tmp", file.filename)
        file.save(file_path)
        problem = cvrp_io.read_TSPLIB_CVRP(file_path)
        os.remove(file_path)
        return jsonify({
            "capacity": problem.capacity_constraint,
            "locations": problem.customer_coordinates,
            "demands": problem.customer_demands
        })

if __name__ == '__main__':
    app.run(debug=True)
```

