        // Format the distance matrix and demands similar to the .vrp file
        let formattedData = "NODE_COORD_SECTION\n";
        distanceMatrix.forEach((coords, index) => {
          formattedData += `${index + 1} ${coords[0]} ${coords[1]}\n`;
        });
        formattedData += "DEMAND_SECTION\n";
        customerDemands.forEach((demand, index) => {
