document.getElementById("solve").addEventListener("click", function () {
  const algorithm = document.getElementById("algorithm").value;
  const capacity = parseInt(document.getElementById("capacity").value, 10);
  const distanceMatrix = document.getElementById("distance-matrix").value;

  if (!algorithm) {
    alert("Please select an algorithm.");
    return;
  }
  if (isNaN(capacity) || capacity <= 0) {
    alert("Please enter a valid number for vehicle capacity.");
    return;
  }
  if (!distanceMatrix.trim()) {
    alert("Please add at least one location.");
    return;
  }

  const locationsArray = distanceMatrix.split("\n").map((location) => {
    const coords = location.replace(/[()]/g, "").split(",");
    const x = parseFloat(coords[0]);
    const y = parseFloat(coords[1]);
    if (isNaN(x) || isNaN(y)) {
      throw new Error("Invalid location coordinates");
    }
    return { x: x, y: y };
  });

  // Convert locations to distance matrix and demands
  const distance_matrix = locationsArray.map((loc1, i) =>
    locationsArray.map((loc2, j) =>
      Math.sqrt(Math.pow(loc1.x - loc2.x, 2) + Math.pow(loc1.y - loc2.y, 2))
    )
  );

  const customer_demands = locationsArray.map(() => 1); // Example: all demands are 1

  const data = {
    algorithm: algorithm,
    distance_matrix: distance_matrix,
    customer_demands: customer_demands,
    capacity_constraint: capacity,
  };

  fetch("/run", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  })
    .then((response) => response.json())
    .then((data) => {
      document.getElementById("result").innerText = JSON.stringify(data.solution, null, 2);
      alert("Solution received. Check the result area.");
    })
    .catch((error) => {
      console.error("Error:", error);
      alert("An error occurred: " + error.message);
    });
});

document.getElementById("add-location").addEventListener("click", function () {
  const xCoord = document.getElementById("x-coord").value;
  const yCoord = document.getElementById("y-coord").value;
  const distanceMatrix = document.getElementById("distance-matrix");
  if (xCoord && yCoord) {
    distanceMatrix.value += `(${xCoord}, ${yCoord})\n`;
  } else {
    alert("Please enter both X and Y coordinates.");
  }
});

document.getElementById("reset").addEventListener("click", function () {
  document.getElementById("vrp-file").value = "";
  document.getElementById("capacity").value = "";
  document.getElementById("x-coord").value = "";
  document.getElementById("y-coord").value = "";
  document.getElementById("distance-matrix").value = "";
  document.getElementById("algorithm").value = "";
});
